targets.expr:
histogram_quantile(0.99, sum(rate(
    tikv_storage_engine_async_request_duration_seconds_bucket
    {k8s_cluster="$k8s_cluster", tidb_cluster="$tidb_cluster", instance=~"$instance", type="snapshot"}
    [$__rate_interval]
)) by (le))

